import { fork, take, call } from "redux-saga/effects";
import TypeAction from "../../constants/index";

function* getHome(){
    while(true){
        const action = yield take(TypeAction.LOGIN);
        const { data } = action;
        const result = yield call(fetch, { ...data });
    }
}

function* homeSaga(){
    yield fork(getHome);
}

export default homeSaga;
