import { configureStore as createToolkitStore } from '@reduxjs/toolkit';
import createSagaMiddleware from 'redux-saga';
import rootReducer from '../Reducers';
// import rootSaga from '../Sagas';

const configureStore = () => {
  // Tạo sagaMiddleware nếu bạn muốn sử dụng redux-saga
  const sagaMiddleware = createSagaMiddleware();

  // Cấu hình store với redux-toolkit
  const store = createToolkitStore({
    reducer: rootReducer,
    middleware: (getDefaultMiddleware) => 
      getDefaultMiddleware().concat(sagaMiddleware),
    devTools: true, // Bật Redux DevTools Extension
  });

  // Chạy rootSaga nếu bạn sử dụng redux-saga
  // sagaMiddleware.run(rootSaga);

  return store;
};

export default configureStore;
