import { fork, take, call, put } from "redux-saga/effects";
import TypeAction from "../../constants/index";
import axios from "axios";

async function getData(data){
    const response = await axios.get(`https://jsonplaceholder.typicode.com/todos/1`);
    const result = response.data;
    // console.log(result);
    kq = Int32Array(data) + 1
    return {"count": kq}
}

function* getHome(){
    while(true){
        const action = yield take(TypeAction.LOGIN);
        const { data } = action;
        const result = yield call(getData, data);
        console.log(result, "saga");
        yield put({type: TypeAction.LOGIN, payload: result});
    }
}

function* homeSaga(){
    yield fork(getHome);
}

export default homeSaga;
